mapper.select:
SELECT count(*) FROM mc_approval_process


        AND dept_code = #{deptCode,jdbcType=VARCHAR}


        AND status = #{status,jdbcType=INTEGER}


        AND resource_id = #{resourceId,jdbcType=BIGINT}


        AND resource_type = #{resourceType,jdbcType=INTEGER}

