mapper.select:
SELECT ap.*, se.ip 'ip', se.`name` 'server_name', se.hostname 'hostname', da.belong 'belong',
sc.name 'schema_name', sc.name_cn 'schema_name_cn', sc.org_code 'schema_org_code',
md.metadata_name 'metadata_name', md.identification 'metadata_identification', md.database_type 'metadata_database_type', md.resource_type 'metadata_resource_type' FROM mc_approval_process ap LEFT JOIN ( select id, `name` as 'metadata_name', `identification`, `database_type`, `resource_type`, `schema_id` from mc_metadata UNION select id, `name`, `identification`, 8, null, `schema_id` from mc_metadata_es ) md ON md.id = ap.resource_id LEFT JOIN mc_schema sc ON sc.id = md.schema_id LEFT JOIN mc_database da ON sc.db_id = da.id LEFT JOIN mc_server se ON da.server_id = se.id where ap.resource_type = md.database_type
AND ap.status IN
#{sta}
AND
FIND_IN_SET(#{resourceOrganisationCode}, sc.org_code)
AND md.database_type = #{databaseType}
AND ap.creator = #{creator}
AND DATE_FORMAT(ap.create_time, '%Y-%m-%d') = DATE_FORMAT(#{createTime}, '%Y-%m-%d')
AND FIND_IN_SET(#{ascriptionDeptCode}, ap.dept_code)
AND ap.renter_id = #{renterId}
ORDER BY ap.modify_time DESC

SELECT ap.*, se.ip 'ip', se.`name` 'server_name', se.hostname 'hostname', da.belong 'belong',
sc.name 'schema_name', sc.name_cn 'schema_name_cn', sc.org_code 'schema_org_code',
md.metadata_name 'metadata_name', md.identification 'metadata_identification', md.database_type 'metadata_database_type', md.resource_type 'metadata_resource_type' FROM mc_approval_process ap LEFT JOIN ( select id, `name` as 'metadata_name', `identification`, `database_type`, `resource_type`, `schema_id` from mc_metadata UNION select id, `name`, `identification`, 8, null, `schema_id` from mc_metadata_es ) md ON md.id = ap.resource_id LEFT JOIN mc_schema sc ON sc.id = md.schema_id LEFT JOIN mc_database da ON sc.db_id = da.id LEFT JOIN mc_server se ON da.server_id = se.id where ap.resource_type = md.database_type
AND ap.status IN
#{sta}
AND ap.creator = #{creator}
AND ap.renter_id = #{renterId}
AND DATE_FORMAT(ap.create_time, '%Y-%m-%d') = DATE_FORMAT(#{createTime}, '%Y-%m-%d')
AND FIND_IN_SET(#{ascriptionDeptCode}, sc.org_code)
ORDER BY ap.modify_time DESC

SELECT sc.id 'schema_id', md.id 'resource_id', da.type AS database_type FROM mc_approval_process ap LEFT JOIN ( select id, `name` as 'metadata_name', `identification`, `database_type`, `resource_type`, `schema_id` from mc_metadata UNION select id, `name`, `identification`, 8, null, `schema_id` from mc_metadata_es ) md ON md.id = ap.resource_id LEFT JOIN mc_schema sc ON sc.id = md.schema_id LEFT JOIN mc_database da ON sc.db_id = da.id LEFT JOIN mc_server se ON da.server_id = se.id WHERE ap.`status` = 2 AND FIND_IN_SET(#{deptCode}, ap.dept_code)
AND ap.resource_type in
#{type}
and md.resource_type in
#{type}
and (
ap.auth_value & #{auth} = #{auth}
)

SELECT
a.id,
a.resource_id,
a.resource_type,
GROUP_CONCAT(b.auth_name) as authNames,
GROUP_CONCAT(b.auth_type) as authTypes,
GROUP_CONCAT(b.auth_value) as authValues
FROM
mc_approval_process a
left join mc_resource_auth b ON a.auth_value & b.auth_value


        and a.resource_id=#{resourceId}


        and a.status=#{status}


        and a.dept_code=#{deptCode}


        and b.auth_name=#{authName}


        and (

            a.auth_value & #{auth} = #{auth}

        }


        and a.resource_type in

            #{type}



group by a.id
