mapper.select:
select

from rc_statistics_dept
where rent_id=#{rentId} and resource_id=#{resourceId} and dept_id=#{deptId}

mapper.insert:
insert into rc_statistics_dept (resource_id, dept_id, dept_name,
resource_type, db_count, file_count,
interface_count, rent_id, creator,
create_time, updater, update_time
)
values (#{resourceId,jdbcType=BIGINT}, #{deptId,jdbcType=BIGINT}, #{deptName,jdbcType=VARCHAR},
#{resourceType,jdbcType=VARCHAR}, #{dbCount,jdbcType=BIGINT}, #{fileCount,jdbcType=BIGINT},
#{interfaceCount,jdbcType=BIGINT}, #{rentId,jdbcType=BIGINT}, #{creator,jdbcType=VARCHAR},
#{createTime,jdbcType=TIMESTAMP}, #{updater,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}
)
